import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import NavDropdown from 'react-bootstrap/NavDropdown';
import { useState } from 'react';
export default function VizNavigation() {
  const [state, useState] = useState({
    base_href: "/chicago_crime_viz"
  });

  return (

    <Navbar expand="lg" className="bg-body-tertiary">
      <Container>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="me-auto">
            <Nav.Link href={state.base_href}>Home</Nav.Link>
            <Nav.Link href={state.base_href + "/about-the-data"}>About the Data</Nav.Link>
            <NavDropdown title="Crime Rate Over Time" id="basic-nav-dropdown">
              <NavDropdown.Item href={state.base_href + "/example"}>Over Time (since 2001)</NavDropdown.Item>
              <NavDropdown.Item href={state.base_href + "/example"}>
                Crime Rate and Unemployment in Chicago
              </NavDropdown.Item>
            </NavDropdown>
            <NavDropdown title="Crime Rate by Area">
             {/*Will eventually become a dynamic variable as well.*/}
             {/*Pseudocode:
                Every month, create a new date and set it to the last day of last month.
                From last month, extract the year and month as a string (numeric and convert to text, or otherwise)
                Update the label dynamically on the site.
             */}
              <NavDropdown.Item href={state.base_href + "/example"}>{state.static_location_crime_label}</NavDropdown.Item>
              <NavDropdown.Item href={state.base_href + "/example"}>Neighborhoods by Year and Crime Rate</NavDropdown.Item>
            </NavDropdown>
          </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}
