import { redirect } from "react-router-dom";

const loader = async () => {
    window.addEventListener('load', (event) => {
        console.log('The page has fully loaded');
    });
    window.onload = (event) => {
        console.log('The page has fully loaded');
        redirect("/chicago_crime_viz")
    };
};
